# s = 'aba'  # True
# s = 'abca'  # True
# s = "abc"  # False

# False
# s = "pidbliassaqozokmtgahluruufwbjdtayuhbxwoicviygilgzduudzgligyviciowxbhuyatdjbwfuurulhagtmkozoqassailbdip"

# s = "cbbcc"  # True

# s = "eddze"  # True

s = """"""


class Solution:
    def validPalindrome(self, s: str) -> bool:
        a = [k for k in s]
        inverted_s = [k for k in s[::-1]]

        if ''.join(a) == ''.join(inverted_s):
            return True

        incorrects = []
        for c in range(len(s)):
            if s[c] != inverted_s[c]:
                incorrects.append(c)

        for incorrect in incorrects:
            aaa = [k for i, k in enumerate(a) if i != incorrect]
            aaaa = [k for i, k in enumerate(inverted_s) if i != (len(s) - incorrect) - 1]
            if ''.join(aaa) == ''.join(aaaa):
                return True
        return False


if __name__ == '__main__':
    obj = Solution()
    response = obj.validPalindrome(s)
    print(response)
